   857→                if (path.startsWith('/') && article.baseURI.endsWith('/')) {
   858→                  fetchUrl = article.baseURI + path.substring(1);
   859→                } else {
   860→                  fetchUrl = article.baseURI + path;
   861→                }
   862→                console.log('Converting image URL using baseURI:', src, '->', fetchUrl);
   863→              } catch (e) {
