   865→              }
   866→            }
   867→          }
   868→        }
   869→      }
   870→
   871→      const response = await fetch(fetchUrl);
   872→      const blob = await response.blob();
   873→
   874→      if (options.imageStyle == 'base64') {
   875→        const reader = new FileReader();
   876→        await new Promise((resolve, reject) => {
   877→          reader.onloadend = resolve;
