   291→// function to convert the article content to markdown using Turndown
   292→function turndown(content, options, article) {
   293→
   294→  if (options.turndownEscape) TurndownService.prototype.escape = TurndownService.prototype.defaultEscape;
   295→  else TurndownService.prototype.escape = s => s;
   296→
   297→  var turndownService = new TurndownService(options);
   298→
   299→  turndownService.use(turndownPluginGfm.gfm)
   300→
   301→  turndownService.keep(['iframe', 'sub', 'sup', 'u', 'ins', 'del', 'small', 'big']);
   302→
   303→  let imageList = {};
   304→  // add an image rule
   305→  turndownService.addRule('images', {
   306→    filter: function (node, tdopts) {
   307→      // if we're looking at an img node with a src
   308→      if (node.nodeName == 'IMG' && node.getAttribute('src')) {
   309→
   310→        // get the original src
   311→        let src = node.getAttribute('src')
   312→        // set the new src
   313→        const validatedSrc = validateUri(src, article.baseURI);
   314→        node.setAttribute('src', validatedSrc);
   315→
   316→        // if we're downloading images, there's more to do.
   317→        if (options.downloadImages) {
   318→          // generate a file name for the image
   319→          let imageFilename = getImageFilename(src, options, false);
   320→          if (!imageList[src] || imageList[src] != imageFilename) {
   321→            // if the imageList already contains this file, add a number to differentiate
   322→            let i = 1;
   323→            while (Object.values(imageList).includes(imageFilename)) {
   324→              const parts = imageFilename.split('.');
   325→              if (i == 1) parts.splice(parts.length - 1, 0, i++);
   326→              else parts.splice(parts.length - 2, 1, i++);
   327→              imageFilename = parts.join('.');
   328→            }
   329→            // add it to the list of images to download later
   330→            imageList[src] = imageFilename;
   331→          }
   332→          // check if we're doing an obsidian style link
   333→          const obsidianLink = options.imageStyle.startsWith("obsidian");
   334→          // figure out the (local) src of the image
   335→          const localSrc = options.imageStyle === 'obsidian-nofolder'
   336→            // if using "nofolder" then we just need the filename, no folder
   337→            ? imageFilename.substring(imageFilename.lastIndexOf('/') + 1)
   338→            // otherwise we may need to modify the filename to uri encode parts for a pure markdown link
   339→            : imageFilename.split('/').map(s => obsidianLink ? s : encodeURI(s)).join('/')
   340→
   341→          // set the new src attribute to be the local filename
   342→          if (options.imageStyle != 'originalSource' && options.imageStyle != 'base64') node.setAttribute('src', localSrc);
   343→          // pass the filter if we're making an obsidian link (or stripping links)
   344→          return true;
   345→        }
   346→        else return true
   347→      }
   348→      // don't pass the filter, just output a normal markdown link
   349→      return false;
   350→    },
   351→    replacement: function (content, node, tdopts) {
   352→      // if we're stripping images, output nothing
   353→      if (options.imageStyle == 'noImage') return '';
   354→      // if this is an obsidian link, so output that
   355→      else if (options.imageStyle.startsWith('obsidian')) return `![[${node.getAttribute('src')}]]`;
   356→      // otherwise, a normal markdown link
   357→      else {
   358→        var alt = cleanAttribute(node.getAttribute('alt'));
   359→        var src = node.getAttribute('src') || '';
   360→        var title = cleanAttribute(node.getAttribute('title'));
   361→        var titlePart = title ? ' "' + title + '"' : '';
   362→        if (options.imageRefStyle == 'referenced') {
   363→          var id = this.references.length + 1;
   364→          this.references.push('[fig' + id + ']: ' + src + titlePart);
   365→          return '![' + alt + '][fig' + id + ']';
   366→        }
   367→        else return src ? '![' + alt + ']' + '(' + src + titlePart + ')' : ''
   368→      }
   369→    },
   370→    references: [],
   371→    append: function (options) {
   372→      var references = '';
   373→      if (this.references.length) {
   374→        references = '\n\n' + this.references.join('\n') + '\n\n';
   375→        this.references = []; // Reset references
   376→      }
   377→      return references
   378→    }
   379→
   380→  });
   381→
   382→  // add a rule for links
   383→  turndownService.addRule('links', {
   384→    filter: (node, tdopts) => {
