   467→    }
   468→  });
   469→
   470→  // handle <pre> as code blocks
   471→  turndownService.addRule('pre', {
   472→    filter: (node, tdopts) => {
   473→      return node.nodeName == 'PRE'
   474→        && (!node.firstChild || node.firstChild.nodeName != 'CODE')
   475→        && !node.querySelector('img');
   476→    },
   477→    replacement: (content, node, tdopts) => {
