   482→  let markdown = options.frontmatter + turndownService.turndown(content)
   483→    + options.backmatter;
   484→
   485→  // strip out non-printing special characters which CodeMirror displays as a red dot
   486→  // see: https://codemirror.net/doc/manual.html#option_specialChars
   487→  markdown = markdown.replace(/[\u0000-\u0008\u000b\u000c\u000e-\u001f\u007f-\u009f\u00ad\u061c\u200b-\u200f\u2028\u2029\ufeff\ufff9-\ufffc]/g, '');
   488→
   489→  return { markdown: markdown, imageList: imageList };
   490→}
