   496→// URL parsing cache to avoid repeated parsing of the same URLs
   497→const urlParseCache = new Map();
   498→
   499→// Safe URL parsing function to handle malformed URLs with caching
   500→function safeUrlParse(urlString, baseUrl = null) {
   501→  // Create cache key
   502→  const cacheKey = baseUrl ? `${urlString}|${baseUrl}` : urlString;
   503→
   504→  // Check cache first
   505→  if (urlParseCache.has(cacheKey)) {
   506→    return urlParseCache.get(cacheKey);
   507→  }
   508→
   509→  let result = null;
   510→  try {
   511→    if (baseUrl) {
   512→      result = new URL(urlString, baseUrl);
   513→    } else {
   514→      result = new URL(urlString);
   515→    }
   516→  } catch (error) {
   517→    // Handle error
   518→  }
   519→
   520→  // Cache the result (including null for failed parses)
   521→  urlParseCache.set(cacheKey, result);
   522→
   523→  // Limit cache size to prevent memory leaks
   524→  if (urlParseCache.size > 1000) {
   525→    const firstKey = urlParseCache.keys().next().value;
   526→    urlParseCache.delete(firstKey);
   527→  }
   528→
   529→  return result;
   530→}
