   532→// Helper function to check if a link is a pure anchor link (starts with #)
   533→function isAnchorLink(href) {
   534→  if (!href || typeof href !== 'string') {
   535→    return false;
   536→  }
   537→  return href.startsWith('#') && href.length > 1;
   538→}
   539→
   540→// Helper function to check if a link is relative to the current page with an anchor
   541→function isRelativeToCurrentPage(href) {
   542→  if (!href || typeof href !== 'string') {
   543→    return false;
   544→  }
   545→
   546→  // Check for patterns like "page.html#section" or "./page.html#section" or "../page.html#section"
   547→  // but not absolute URLs or protocol-relative URLs
   548→  if (href.includes('://') || href.startsWith('//')) {
   549→    return false;
   550→  }
   551→
   552→  // Check if it contains an anchor
   553→  const hashIndex = href.indexOf('#');
   554→  if (hashIndex === -1) {
   555→    return false;
   556→  }
   557→
   558→  // If it starts with #, it's a pure anchor link
   559→  if (hashIndex === 0) {
   560→    return true;
   561→  }
   562→
   563→  // Check if the part before # is a relative path
   564→  const pathPart = href.substring(0, hashIndex);
   565→  return !pathPart.startsWith('/') && !pathPart.includes('://');
   566→}
   567→
   568→// Helper function to validate anchor link validity
   569→function validateAnchorLink(href, baseURI) {
   570→  if (!href || typeof href !== 'string') {
   571→    return false;
   572→  }
   573→
   574→  try {
   575→    // Pure anchor links are always valid if they have content after #
   576→    if (isAnchorLink(href)) {
   577→      return href.length > 1; // Must have content after #
   578→    }
   579→
   580→    // For relative links with anchors, validate the base part
   581→    if (isRelativeToCurrentPage(href)) {
   582→      const hashIndex = href.indexOf('#');
   583→      if (hashIndex > 0) {
   584→        // Validate that we can construct a valid URL with the base URI
   585→        const pathPart = href.substring(0, hashIndex);
   586→        const baseUrl = new URL(baseURI);
   587→        new URL(pathPart, baseUrl);
   588→        return true;
   589→      }
   590→      return hashIndex === 0 && href.length > 1;
   591→    }
   592→
   593→    // For other types of links, try to parse as URL
   594→    new URL(href);
   595→    return true;
   596→  } catch (error) {
   597→    return false;
   598→  }
   599→}
   600→
   601→function validateUri(href, baseURI) {
   602→  // CRITICAL FIX: If baseURI starts with chrome-extension://, try to extract a proper base
   603→  if (baseURI && baseURI.startsWith('chrome-extension://')) {
   604→    // Try to extract domain from article properties or use a fallback
   605→    if (article && article.originalUrl) {
   606→      try {
   607→        // Try to use originalUrl as baseURI
   608→        const url = new URL(article.originalUrl);
   609→        baseURI = url.origin + '/';
   610→      } catch (e) {
   611→        // If parsing fails, try other methods
   612→      }
   613→    }
   614→
   615→    // If originalUrl didn't work, try other article properties
   616→    if (baseURI.startsWith('chrome-extension://')) {
   617→      if (article && article.origin) {
   618→        baseURI = article.origin + '/';
   619→      } else if (article && article.hostname) {
   620→        baseURI = 'https://' + article.hostname + '/';
   621→      } else if (article && article.baseURI && !article.baseURI.startsWith('chrome-extension://')) {
   622→        baseURI = article.baseURI;
   623→      } else {
   624→        // Last resort - try to extract domain from canonical link or meta tags
   625→        try {
   626→          const dom = document.implementation.createHTMLDocument();
   627→          dom.documentElement.innerHTML = article.content || '';
   628→
   629→          // Try canonical link
   630→          const canonicalLink = dom.querySelector('link[rel="canonical"]');
   631→          if (canonicalLink && canonicalLink.href) {
   632→            const url = new URL(canonicalLink.href);
   633→            baseURI = url.origin + '/';
   634→          }
   635→          // Try og:url meta tag
   636→          else {
   637→            const ogUrl = dom.querySelector('meta[property="og:url"]');
   638→            if (ogUrl && ogUrl.content) {
   639→              const url = new URL(ogUrl.content);
   640→              baseURI = url.origin + '/';
   641→            }
   642→          }
   643→        } catch (e) {
   644→          // If all else fails, use a generic fallback
   645→          baseURI = 'https://example.com/';
   646→        }
   647→      }
   648→    }
   649→  }
   650→
   651→  // Special handling for chrome-extension:// links
   652→  if (href && href.startsWith('chrome-extension://')) {
   653→    // Extract the path part after the extension ID
   654→    const pathMatch = href.match(/chrome-extension:\/\/[^\/]+(.+)/);
   655→    if (pathMatch && pathMatch[1]) {
   656→      const path = pathMatch[1];
   657→
   658→      // Try to construct a URL using the baseURI
   659→      if (baseURI) {
   660→        try {
   661→          // Remove leading slash if baseURI ends with slash
   662→          if (path.startsWith('/') && baseURI.endsWith('/')) {
   663→            return baseURI + path.substring(1);
   664→          }
   665→          return baseURI + path;
   666→        } catch (e) {
   667→          // If construction fails, continue with other methods
   668→        }
   669→      }
   670→    }
   671→
   672→    // We already handled this case above, no need to duplicate code
   673→  }
   674→
   675→
   676→  // Special handling for anchor links
   677→  if (isAnchorLink(href)) {
   678→    // For pure anchor links (#section), return directly without any conversion
   679→    return href;
   680→  }
   681→
   682→  if (isRelativeToCurrentPage(href)) {
   683→    const hashIndex = href.indexOf('#');
   684→    if (hashIndex > 0) {
   685→      // For relative links with anchors, extract and preserve the anchor part
   686→      const pathPart = href.substring(0, hashIndex);
   687→      const anchorPart = href.substring(hashIndex);
   688→
   689→      try {
   690→        // Validate the path part can be resolved
   691→        const baseUrl = safeUrlParse(baseURI);
   692→        if (baseUrl) {
   693→          const resolvedUrl = safeUrlParse(pathPart, baseUrl);
   694→          if (resolvedUrl) {
   695→            // Return the relative format with anchor preserved
   696→            return pathPart + anchorPart;
   697→          }
   698→        }
   699→        // If path resolution fails, return as-is
   700→        return href;
   701→      } catch (error) {
   702→        return href;
   703→      }
   704→    } else if (hashIndex === 0) {
   705→      // Pure anchor link, return as-is
   706→      return href;
   707→    }
   708→  }
   709→
   710→  // Original logic for other types of links with enhanced error handling
   711→  const parsedUrl = safeUrlParse(href);
   712→  if (parsedUrl) {
   713→    // If it's already a valid absolute URL, return as-is
   714→    return href;
   715→  } else {
   716→    // if it's not a valid url, that likely means we have to prepend the base uri
   717→    const baseUrl = safeUrlParse(baseURI);
   718→    if (!baseUrl) {
   719→      return href; // Return original href if base URI is invalid
   720→    }
   721→
   722→    try {
   723→      // if the href starts with '/', we need to go from the origin
   724→      if (href.startsWith('/')) {
   725→        href = baseUrl.origin + href;
   726→      }
   727→      // otherwise we need to go from the local folder
   728→      else {
   729→        href = baseUrl.href + (baseUrl.href.endsWith('/') ? '' : '/') + href;
   730→      }
   731→    } catch (error) {
   732→      return href; // Return original href on error
   733→    }
   734→  }
   735→  return href;
   736→}
   737→
   738→function getImageFilename(src, options, prependFilePath = true) {
   739→  const slashPos = src.lastIndexOf('/');
   740→  const queryPos = src.indexOf('?');
   741→  let filename = src.substring(slashPos + 1, queryPos > 0 ? queryPos : src.length);
   742→
   743→  let imagePrefix = (options.imagePrefix || '');
   744→
   745→  // 完全禁用使用页面标题作为图片文件名前缀
   746→  // 这可以避免长标题导致的文件名问题
   747→  // 用户可以通过设置 options.imagePrefix 来自定义前缀
   748→
   749→  if (filename.includes(';base64,')) {
   750→    // this is a base64 encoded image, so what are we going to do for a filename here?
   751→    filename = 'image.' + filename.substring(0, filename.indexOf(';'));
   752→  }
   753→
   754→  let extension = filename.substring(filename.lastIndexOf('.'));
   755→  if (extension == filename) {
   756→    // there is no extension, so we need to figure one out
   757→    // for now, give it an 'idunno' extension and we'll process it later
   758→    filename = filename + '.idunno';
   759→    extension = '.idunno';
   760→  }
   761→
   762→  // Check if filename is too long or contains problematic characters
   763→  const baseName = filename.substring(0, filename.lastIndexOf('.'));
   764→  const maxSafeLength = 60; // More conservative limit for better compatibility
   765→
   766→  if (baseName.length > maxSafeLength || /[^\w\-_.]/.test(baseName)) {
   767→    // Generate UUID-based filename for problematic cases
   768→    const uuid = generateUUID();
   769→    filename = uuid + extension;
   770→    console.log(`Using UUID filename for problematic image: ${src} -> ${filename}`);
   771→  } else {
   772→    filename = generateValidFileName(filename, options.disallowedChars);
   773→  }
   774→
   775→  return imagePrefix + filename;
   776→}
   777→
   778→// Generate a simple UUID v4
   779→function generateUUID() {
   780→  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
   781→    const r = Math.random() * 16 | 0;
   782→    const v = c == 'x' ? r : (r & 0x3 | 0x8);
   783→    return v.toString(16);
   784→  });
   785→}
   786→
   787→// function to turn the title into a valid file name
   788→function generateValidFileName(title, disallowedChars = null) {
   789→  if (!title) return title;
   790→  else title = title + '';
   791→  // remove < > : " / \ | ? * 
   792→  var illegalRe = /[\/\?<>\\:\*\|":]/g;
   793→  // and non-breaking spaces (thanks @Licat)
   794→  var name = title.replace(illegalRe, "").replace(new RegExp('\u00A0', 'g'), ' ')
   795→    // collapse extra whitespace
   796→    .replace(new RegExp(/\s+/, 'g'), ' ')
   797→    // remove leading/trailing whitespace that can cause issues when using {pageTitle} in a download path
   798→    .trim();
   799→
   800→  if (disallowedChars) {
   801→    for (let c of disallowedChars) {
   802→      if (`[\\^$.|?*+()`.includes(c)) c = `\\${c}`;
   803→      name = name.replace(new RegExp(c, 'g'), '');
   804→    }
   805→  }
   806→
   807→  // Limit filename length to avoid filesystem limitations
   808→  // Windows has a 255 character limit for filenames
   809→  const maxLength = 200; // Leave some room for extensions and paths
   810→  if (name.length > maxLength) {
   811→    // Try to preserve the file extension if it exists
   812→    const lastDotIndex = name.lastIndexOf('.');
   813→    if (lastDotIndex > 0 && lastDotIndex > name.length - 10) {
   814→      // Extension exists and is reasonable length
   815→      const extension = name.substring(lastDotIndex);
   816→      const baseName = name.substring(0, lastDotIndex);
   817→      name = baseName.substring(0, maxLength - extension.length) + extension;
   818→    } else {
   819→      // No extension or extension is too long, just truncate
   820→      name = name.substring(0, maxLength);
   821→    }
   822→    console.log(`Filename truncated to ${name.length} characters: ${name}`);
   823→  }
   824→
   825→  return name;
   826→}
   827→
   828→async function preDownloadImages(imageList, markdown, options) {
   829→  let newImageList = {};
   830→  await Promise.all(Object.entries(imageList).map(async ([src, filename]) => {
   831→    try {
   832→      // Convert chrome-extension URLs to proper URLs before fetching
   833→      let fetchUrl = src;
   834→      if (src.startsWith('chrome-extension://')) {
   835→        // Use the same logic as validateUri to convert chrome-extension URLs
   836→        // Generic pattern for GitHub-like paths
   837→        const githubPathMatch = src.match(/\/([^\/]+)\/([^\/]+)\/(raw|blob)\/([^\/]+)\/(.+)/);
   838→        if (githubPathMatch) {
   839→          const user = githubPathMatch[1];
   840→          const repo = githubPathMatch[2];
   841→          const type = githubPathMatch[3]; // raw or blob
   842→          const branch = githubPathMatch[4]; // master, main, etc.
   843→          const path = githubPathMatch[5]; // file path
   844→
   845→          // Convert to GitHub URL
   846→          fetchUrl = `https://github.com/${user}/${repo}/${type}/${branch}/${path}`;
   847→          console.log('Converting image URL for fetch:', src, '->', fetchUrl);
   848→        } else {
   849→          // Try to use the baseURI from the article if available
   850→          if (article && article.baseURI) {
   851→            const generalPathMatch = src.match(/chrome-extension:\/\/[^\/]+(.+)/);
   852→            if (generalPathMatch) {
   853→              const path = generalPathMatch[1];
   854→              // Try to construct a URL using the baseURI
   855→              try {
   856→                // Remove leading slash if baseURI ends with slash
   857→                if (path.startsWith('/') && article.baseURI.endsWith('/')) {
   858→                  fetchUrl = article.baseURI + path.substring(1);
   859→                } else {
   860→                  fetchUrl = article.baseURI + path;
   861→                }
   862→                console.log('Converting image URL using baseURI:', src, '->', fetchUrl);
   863→              } catch (e) {
   864→                console.warn('Failed to convert image URL:', src, e);
   865→              }
   866→            }
   867→          }
   868→        }
   869→      }
   870→
   871→      const response = await fetch(fetchUrl);
   872→      const blob = await response.blob();
   873→
   874→      if (options.imageStyle == 'base64') {
   875→        const reader = new FileReader();
   876→        await new Promise((resolve, reject) => {
   877→          reader.onloadend = resolve;
   878→          reader.onerror = reject;
   879→          reader.readAsDataURL(blob);
   880→        });
   881→        markdown = markdown.replaceAll(src, reader.result);
   882→      } else {
   883→        let newFilename = filename;
   884→
   885→        // Check if we need to update the file extension based on actual MIME type
   886→        if (newFilename.endsWith('.idunno') || blob.type && mimedb[blob.type]) {
   887→          let correctExtension = mimedb[blob.type];
   888→
   889→          // Handle special case for JPEG (mimedb uses "jpeg" but we prefer "jpg")
   890→          if (correctExtension === 'jpeg') {
   891→            correctExtension = 'jpg';
   892→          }
   893→
   894→          if (correctExtension) {
   895→            // Get the current extension
   896→            const currentExtension = newFilename.substring(newFilename.lastIndexOf('.') + 1).toLowerCase();
   897→
   898→            // If extensions don't match, update the filename
   899→            if (currentExtension !== correctExtension && currentExtension !== 'idunno') {
   900→              console.log(`Image format mismatch: expected ${currentExtension}, got ${blob.type} (${correctExtension})`);
   901→              newFilename = newFilename.substring(0, newFilename.lastIndexOf('.')) + '.' + correctExtension;
   902→            } else if (currentExtension === 'idunno') {
   903→              newFilename = filename.replace('.idunno', '.' + correctExtension);
   904→            }
   905→
   906→            // Update markdown references if filename changed
   907→            if (newFilename !== filename) {
   908→              if (!options.imageStyle.startsWith("obsidian")) {
   909→                markdown = markdown.replaceAll(filename.split('/').map(s => encodeURI(s)).join('/'), newFilename.split('/').map(s => encodeURI(s)).join('/'))
   910→              } else {
   911→                markdown = markdown.replaceAll(filename, newFilename)
   912→              }
   913→            }
   914→          }
   915→        }
   916→
   917→        const blobUrl = URL.createObjectURL(blob);
   918→        newImageList[blobUrl] = newFilename;
   919→      }
   920→    } catch (error) {
   921→      console.error('A network error occurred attempting to download ' + src, error);
   922→    }
   923→  }));
   924→
   925→  return { imageList: newImageList, markdown: markdown };
   926→}

